    /*
        ADD UNIVERSITY TO TABLE
    */
    private void btnAddActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAddActionPerformed
        boolean bName = false, bAddress = false;
        
        if (txtName.getText().equals("")) {
            lblNameErr.setText("Please enter a name");
        } else {
            bName = true;
            lblNameErr.setText("");
        }
        if (txtAddress.getText().equals("")) {
            lblAddressErr.setText("Please enter an address");
        } else {
            bAddress = true;
            lblAddressErr.setText("");
        }
        
        if (bName && bAddress) {
            // ADD THE UNIVERSITY TO THE DATABASE AND LIST
            
            
            lblAdded.setText("University has been added!");
        }
    }//GEN-LAST:event_btnAddActionPerformed

